mapper.select:
select count(*)
from neighbor
where mymemail = #{memail}

select rnum, nno, mymemail, nememail, ndate, nemurl, mmyimage, mnickname
from (
	select rownum as rnum, nno, mymemail, nememail, ndate, nemurl, mmyimage, mnickname
      from (
      	select nno, mymemail, nememail, ndate, nemurl, mmyimage, mnickname
            from member m, neighbor n
            where m.memail=n.nememail and mymemail=#{murl}
            order by ndate desc
                )
      where rownum <=#{endRowNo}
        )
where rnum >=#{startRowNo}

SELECT count(*)
FROM neighbor
WHERE nemurl=#{userUrl} AND mymemail=#{memail}
