mapper.select:

SELECT memail, mpassword, mnickname, mphonenum, mintro, mmyimage, mrole, menabled, murl
FROM member
WHERE memail = #{memail}

SELECT memail, mpassword, mnickname, mphonenum, mintro, mmyimage, mrole, menabled, murl
FROM member
WHERE murl = #{memail}

SELECT count(*)
FROM member
WHERE memail=#{memail}

SELECT count(*)
FROM member
WHERE mnickname=#{mnickname}
