mapper.select:

SELECT memail, mpassword, mnickname, mphonenum, mintro, mmyimage, mrole, menabled, murl
FROM member
WHERE memail = #{memail}

SELECT memail, mpassword, mnickname, mphonenum, mintro, mmyimage, mrole, menabled, murl
FROM member
WHERE murl = #{memail}

SELECT count(*)
FROM member
WHERE memail=#{memail}

SELECT count(*)
FROM member
WHERE mnickname=#{mnickname}

SELECT memail, mnickname, mmyimage, mrole, mphonenum, murl
FROM member
WHERE mrole=#{mrole}


SELECT memail, menabled, mnickname, mphonenum, mintro, mmyimage
FROM (
	SELECT rownum as rnum, memail, menabled, mnickname, mphonenum, mintro, mmyimage
	FROM member
	WHERE menabled=1 and rownum <=#{endRowNo}
)
WHERE rnum >=#{startRowNo}

	UPDATE member
    SET menabled=0
    WHERE memail=#{memail}

	UPDATE member
    SET menabled=1
    WHERE memail=#{memail}

select memail, mphonenum, mnickname
from member
where mphonenum = #{mphonenum}

select count(*)
from member
where mphonenum = #{mphonenum} and memail = #{memail}
