mapper.select:
select rcontent, rnum, rno, rdate, murl
from (select rownum as rnum, rcontent, rno, rdate, murl
      from (select rcontent, rno, rdate, murl
            from reply r, board b
            where b.bno = r.bno and b.murl = #{murl}
            order by rdate desc
                )
      where rownum <=#{endRowNo}
        )
where rnum >=#{startRowNo}
